import React, { Component } from "react";
import { Col, Row } from "react-bootstrap";
import chica1 from "./img/cable1.png";
import chica2 from "./img/cable2.png";
import chica3 from "./img/cable3.png";
import chica4 from "./img/cable4.png";
import chica5 from "./img/cable5.png";
import "./album.css";

export default class album extends Component {
  constructor(props) {
    super(props);
    this.state = { numPages: 5, pageNumber: 1 };
  }

  onDocumentLoadSuccess = ({ numPages }) => {
    this.setState({ numPages });
  };

  goToPrevPage = () =>
    this.setState((state) => ({ pageNumber: state.pageNumber - 1 }));
  goToNextPage = () =>
    this.setState((state) => ({ pageNumber: state.pageNumber + 1 }));
  goToFirstPage = () => this.setState((state) => ({ pageNumber: 1 }));

  render() {
    const { pageNumber } = this.state;

    return (
      <div>
        <div className="div-group">
          <div className={pageNumber === 1 ? "img-div active" : "img-div"}>
            <img
              className="img-page"
              style={pageNumber === 1 ? { opacity: "1" } : { opacity: "0" }}
              src={chica1}
            ></img>
          </div>
          <div className={pageNumber === 2 ? "img-div active" : "img-div"}>
            <img
              className="img-page"
              style={pageNumber === 2 ? { opacity: "1" } : { opacity: "0" }}
              src={chica2}
            ></img>
          </div>
          <div className={pageNumber === 3 ? "img-div active" : "img-div"}>
            <img
              className="img-page"
              style={pageNumber === 3 ? { opacity: "1" } : { opacity: "0" }}
              src={chica3}
            ></img>
          </div>

          <div className={pageNumber === 4 ? "img-div active" : "img-div"}>
            <img
              className="img-page"
              style={pageNumber === 4 ? { opacity: "1" } : { opacity: "0" }}
              src={chica4}
            ></img>
          </div>
          <div className={pageNumber === 5 ? "img-div active" : "img-div"}>
            <img
              className="img-page"
              style={pageNumber === 5 ? { opacity: "1" } : { opacity: "0" }}
              src={chica5}
            ></img>
          </div>
        </div>
        <div>        
        {pageNumber === 1 ? "" : <div className="btn-album" onClick={this.goToPrevPage}><img src={btn-right}/></div>}
        </div>
        <div>
        {pageNumber === 5 ? "" : <div className="btn-album" onClick={this.goToNextPage}></div>}
        </div>
        {/* <div>
        {pageNumber === 5 ? <button className="btn-first" onClick={this.goToFirstPage}>First</button> : ""}
        </div> */}
      </div>
    );
  }
}
