import React, { Component } from "react";
import { Col, Row } from 'react-bootstrap';
import chica2 from "cable2.png"
import chica1 from "cable1.png"
import "./album.css"


export default class album extends Component {
  constructor(props){
    super(props);
    this.state = { numPages: 21, pageNumber: 4, albumHeight : window.innerHeight };
  }

  onDocumentLoadSuccess = ({ numPages }) => {
    this.setState({ numPages });
  };

  goToPrevPage = () =>
  this.setState(state => ({ pageNumber: state.pageNumber - 1 }));
  goToNextPage = () =>
  this.setState(state => ({ pageNumber: state.pageNumber + 1 }));
  goToFirstPage = () =>
  this.setState(state => ({ pageNumber: 1 }));




  render() {

    const { pageNumber,albumHeight } = this.state;
console.log("pageNumber",pageNumber)
    return (
      <Row>
               {/* <Row style={{display:"grid", justifyContent:"center", margin:"0", height:albumHeight}}></Row> */}
      {/* <Col xs={12} style={{margin:"10px"}}>
      {pageNumber === 1 ? "" : <button  onClick={this.goToPrevPage}>Prev</button>}
        {pageNumber === 4 ? "" : <button  onClick={this.goToNextPage}>Next</button>}
        {pageNumber === 4 ? <button  onClick={this.goToFirstPage}>First</button> : ""}
      </Col> */}

        {/* <Col xs={6} style={{margin:"20px"}}>
          <button onClick={this.goToPrevPage}>Prev</button>
          <button onClick={this.goToNextPage}>Next</button>
        </Col> */}

        {/* <Col xs={12} style={{padding:"0"}}>
       {pageNumber === 1 ? <img style={{maxWidth: "100%"}} src={chica1}></img> : ""}
       {pageNumber === 2 ? <img style={{maxWidth: "100%"}} src={chica2}></img> : ""}
       {pageNumber === 3 ? <img style={{maxWidth: "100%"}} src={chica1}></img> : ""}
       {pageNumber === 4 ? <img style={{maxWidth: "100%"}} src={chica2}></img> : ""}
        </Col> */}

     <div xs={12} className="container"> 
     <div className={pageNumber === 1 ? "item active" : "item"}>
       <img className="img-bg" style={pageNumber === 1 ? {opacity:"1"} : {opacity:"0"}} src={chica1}></img> 
     </div>
       <div className={pageNumber === 2 ? "item active" : "item"}>

      <img className="img-bg" style={pageNumber === 2 ? {opacity:"1"} : {opacity:"0"}} src={chica2}></img> 
       </div>
      <div className={pageNumber === 3 ? "item active" : "item"}>
      <img className="img-bg" style={pageNumber === 3 ? {opacity:"1"} : {opacity:"0"}} src={chica1}></img> 
      </div>

     <div className={pageNumber === 4 ? "item active" : "item"}>
     <img className="img-bg" style={pageNumber === 4 ? {opacity:"1"} : {opacity:"0"}} src={chica2}></img> 
     </div>
        </div>

        <Col xs={12} style={{padding:"0"}}>

        {pageNumber === 1 ? "" : <button className="btn-prev" onClick={this.goToPrevPage}>Prev</button>}
        {pageNumber === 4 ? "" : <button className="btn-next" onClick={this.goToNextPage}>Next</button>}
        {pageNumber === 4 ? <button className="btn-first" onClick={this.goToFirstPage}>First</button> : ""}


        </Col>
      </Row>
    );
  }
}
