import React, { Component } from "react";
import { Document, Page,pdfjs } from "react-pdf";
import { Col, Row } from 'react-bootstrap';
import chicas from "./LasChicasdelCable.pdf"
import "./pdf.css"


export default class pdf extends Component {
  constructor(props){
    super(props);
    pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;
    this.state = { numPages: 21, pageNumber: 1, pdfWidth: window.innerWidth,pdfHeight: window.innerHeight };
  }

  onDocumentLoadSuccess = ({ numPages }) => {
    this.setState({ numPages });
  };

  goToPrevPage = () =>
  this.setState(state => ({ pageNumber: state.pageNumber - 1 }));
  goToNextPage = () =>
  this.setState(state => ({ pageNumber: state.pageNumber + 1 }));
  goToFirstPage = () =>
  this.setState(state => ({ pageNumber: 1 }));




  render() {

    const { pageNumber, numPages, pdfWidth, pdfHeight } = this.state;

    console.log("width",window.innerWidth)
    return (
      <Row style={{display:"grid", justifyContent:"center", margin:"0"}}>

        {/* <Col xs={6}>
          <button onClick={this.goToPrevPage}>Prev</button>
          <button onClick={this.goToNextPage}>Next</button>
        </Col> */}

        <Col xs={12} style={{padding:"0"}}>

        {pageNumber === 1 ? "" : <button className="btn-prev" onClick={this.goToPrevPage}>Prev</button>}
        {pageNumber === 21 ? "" : <button className="btn-next" onClick={this.goToNextPage}>Next</button>}
        {pageNumber === 21 ? <button className="btn-first" onClick={this.goToFirstPage}>First</button> : ""}

          <Document
            file={chicas}
            onLoadSuccess={this.onDocumentLoadSuccess}
          >
            <Page pageNumber={pageNumber} width={pdfWidth} />
          </Document>
        </Col>

        {/* <p>
          Page {pageNumber} of {numPages}
        </p> */}
      </Row>
    );
  }
}
